import React from "react";

class App extends React.Component {
  render() {
    const students = [
      {
        promo: "A",
        name: "Sofía",
        age: 20,
      },
      {
        promo: "B",
        name: "María",
        age: 21,
      },
      {
        promo: "A",
        name: "Lucía",
        age: 22,
      },
    ];

    const promoAStudents = students.filter(
      (student, index) => student.promo === "A"
    );
    console.log(promoAStudents);

    return (
      <div>
        <h1>Pintar listados con React:</h1>
        {promoAStudents.map((student, index) => {
          return (
            <li key={index}>
              <h2>Nombre: {student.name}</h2>
              <p>Edad: {student.age}</p>
            </li>
          );
        })}
      </div>
    );
  }
}

export default App;
