import React from "react";
import fetchUsers from "../services/UsersService";
import UsersList from "./UsersList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      usersData: [],
      filterCity: "",
      filterMale: "",
      filterFemale: "",
      isLoading: true,
    };

    fetchUsers().then((data) => {
      console.log(data);
      this.setState({
        usersData: data.results,
        isLoading: false,
      });
    });

    this.handleFilterCity = this.handleFilterCity.bind(this);
    this.handleFilterGenderMale = this.handleFilterGenderMale.bind(this);
    this.handleFilterGenderFemale = this.handleFilterGenderFemale.bind(this);
  }

  handleFilterCity(ev) {
    this.setState({
      filterCity: ev.target.value,
    });
  }

  handleFilterGenderMale(ev) {
    this.setState({
      filterMale: ev.target.value,
    });
  }

  handleFilterGenderFemale(ev) {
    this.setState({
      filterFemale: ev.target.value,
    });
  }

  renderUsersList() {
    // si quieres ver este método como si fuera un poema abre AppPoem.js
    return this.state.usersData
      .filter((user) => {
        return user.show.name
          .toLowerCase()
          .includes(this.state.filterText.toLowerCase());
      })
      .filter((serie) => {
        return serie.show.language
          .toLowerCase()
          .includes(this.state.filterLanguage.toLowerCase());
      })
      .map((serie) => {
        return <li key={serie.show.id}>Nombre: {serie.show.name}</li>;
      });
  }

  render() {
    return (
      <section>
        <p>{this.state.isLoading ? "Cargando.." : ""}</p>
        <form onClick={this.handleFetch}>
          <input
            type="text"
            placeholder="ciudad"
            onChange={this.handleFilterCity}
          ></input>
          <label htmlFor="male">
            <input
              type="checkbox"
              name="male"
              value="male"
              OnClick={this.handleFilterGenderMale}
            />
            male
          </label>
          <label htmlFor="female">
            <input
              type="checkbox"
              name="female"
              value="female"
              OnClick={this.handleFilterGenderMale}
            />
            female
          </label>
        </form>
      </section>
    );
  }
}

export default App;
