import React from "react";
import { fetchUsers } from "./services/UsersService";
import UsersList from "./UsersList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      usersData: [],
    };

    this.handleFetch = this.handleFetch.bind(this);
  }

  handleFetch(event) {
    event.preventDefault();
    fetchUsers() // Continuamos añadiendo .then() a la Promise del fetch
      .then((data) => {
        const filteredData = data.results.filter(
          (user) => user.location.country === event.target.value
        );
        this.setState({
          usersData: filteredData,
        });
        console.log(filteredData);
      });
  }

  render() {
    return (
      <>
        <section>
          <UsersList usersData={usersData} updateData={this.handleFetch} />
          <form onClick={this.handleFetch} />
        </section>
      </>
    );
  }
}

export default App;
