import "../stylesheets/Card.scss";

const Card = (props) => {
  return (
    <>
      <img
        className="item__img"
        src={props.src}
        alt={props.alt}
        title={props.name}
      />
      <h2 className="item__name">{props.name}</h2>
      <p className="item__description">
        Marianne Joan Elliott-Said (3 July 1957 – 25 April 2011),known by the
        stage name Poly Styrene, was an English musician, singer-songwriter, and
        frontwoman for the punk rock band X-Ray Spex.
      </p>
    </>
  );
};

export default Card;
