import React from "react";
import "../stylesheets/App.scss";
import getDataFromAPI from "../services/api.js";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      series: [],
      filterText: "",
    };

    this.renderSeries = this.renderSeries.bind.this;
    this.handlerFilterTitle = this.handlerFilterTitle.bind.this;

    getDataFromAPI().then((data) => {
      console.log(data);
      this.setState({
        series: data,
      });
    });
  }

  renderSeries() {
    return this.state.series.map((serie) => {
      return (
        <li key={serie.show.id}>
          {serie.show.name} <p> {"Idioma" + serie.show.language}</p>
        </li>
      );
    });
  }

  handlerFilterTitle(event) {
    this.setState({ filterTitle: event.currentTarget.value });
  }

  render() {
    return (
      <div>
        <p>Series</p>
        <input
          placeholder="Buscar series por título"
          onChange={this.handlerFilterTitle}
        ></input>
        <ul>{this.renderSeries()}</ul>
      </div>
    );
  }
}

export default App;
