import React from "react";
import "../stylesheets/App.scss";
import getDataFromAPI from "../services/api.js";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      series: [],
    };

    getDataFromAPI().then((data) => {
      console.log(data);
      this.setState({
        series: data,
      });
    });
  }

  renderSeries() {
    return this.state.series.map((serie) => {
      return (
        <li key={serie.show.id}>
          {serie.show.name} <p> {"Idioma" + serie.show.language}</p>
        </li>
      );
    });
  }

  render() {
    return (
      <div>
        <p>Series</p>
        <ul>{this.renderSeries()}</ul>
      </div>
    );
  }
}

export default App;
