import React, { useState } from "react";
import "../stylesheets/App.scss";
import LikesCounter from "./LikesCounter";

const App = () => {
  const [number, setNumber] = useState(20);

  const handleClick = () => {
    setNumber(number + 1);
  };

  return <LikesCounter handleClick={handleClick} stateNumber={number} />;
};

export default App;
