import React, { useState, useEffect } from "react";

const Text = () => {
  const [userText, setUserText] = useState(
    localStorage.getItem("newText") || ""
  );

  // usamos el hook useEffect para gestionar los ciclos de vida del componente
  useEffect(() => {
    localStorage.setItem("newText", userText);
    setUserText(newText);

    return () => {
      const newText = localStorage.getItem("newText");
    };
  });

  const handleTextArea = (event) => {
    const textAreaValue = event.target.value;
    setUserText(textAreaValue);
  };

  return (
    <textarea
      id="comments"
      name="comments"
      value={userText}
      rows="8"
      cols="80"
      onChange={handleTextArea}
    />
  );
};

export default Text;
