import React from "react";
import fetchUsers from "../services/UsersService";
import UsersList from "./UsersList";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      usersData: [],
      filterCity: "",
      filterGender: "",
      isLoading: true,
    };

    fetchUsers().then((data) => {
      console.log(data);
      this.setState({
        usersData: data,
        isLoading: false,
      });
    });
  }

  render() {
    return (
      <section>
        <p>{this.state.isLoading ? "Cargando.." : ""}</p>
        <form onClick={this.handleFetch}>
          <input
            type="text"
            placeholder="ciudad"
            onChange={this.handleFilterCity}
          ></input>
          <label htmlFor="male">
            <input type="checkbox" name="male" value="male" />
            male
          </label>
          <label htmlFor="female">
            <input type="checkbox" name="female" value="male" />
            female
          </label>
        </form>
      </section>
    );
  }
}

export default App;
