import React from "react";
import "../stylesheets/App.scss";
import SheepCounter from "./SheepCounter";
import SheepImg from "./SheepImg";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 0,
    };
    this.handleClick = this.handleClick.bind(this);
    this.renderSheeps = this.renderSheeps.bind(this);
  }

  handleClick() {
    this.setState((prevState, props) => {
      let newNumber = prevState.number + 1;

      return {
        number: newNumber,
      };
    });
  }

  renderSheeps() {
    const sheeps = [];
    for (let i = 0; i < this.stateNumber; i++) {
      sheeps.push(<SheepImg />);
      console.log(sheeps);
    }
  }

  render() {
    return (
      <>
        <SheepCounter
          handleClick={this.handleClick}
          stateNumber={this.state.number}
        />
        {this.sheeps}
      </>
    );
  }
}

export default App;
