import React, { useState } from "react";
import "../stylesheets/App.scss";
import LikesCounter from "./LikesCounter";

const App = () => {
  const [number, setNumber] = useState(20);
  const generateNewLikes = (number) => number + 1;

  const handleClick = () => {
    setNumber(generateNewLikes());
  };

  return (
    <>
      <LikesCounter handleClick={handleClick} stateNumber={number} />
    </>
  );
};

export default App;
